function FlashSideUI.SetScaleformParams(scaleform, data)
    data = data or {}
    for k, v in pairs(data) do
        PushScaleformMovieFunction(scaleform, v.name)
        if v.param then
            for _, par in pairs(v.param) do
                if math.type(par) == "integer" then
                    PushScaleformMovieFunctionParameterInt(par)
                elseif type(par) == "boolean" then
                    PushScaleformMovieFunctionParameterBool(par)
                elseif math.type(par) == "float" then
                    PushScaleformMovieFunctionParameterFloat(par)
                elseif type(par) == "string" then
                    PushScaleformMovieFunctionParameterString(par)
                end
            end
        end
        if v.func then
            v.func()
        end
        PopScaleformMovieFunctionVoid()
    end
end

---Visible
---@param Menu function
---@param Value boolean
---@return table
---@public
function FlashSideUI.Visible(Menu, Value)
    if Menu ~= nil then
        if Menu() then
            if type(Value) == "boolean" then
                if Value then
                    if FlashSideUI.CurrentMenu ~= nil then
                        FlashSideUI.CurrentMenu.Closed()
                        FlashSideUI.CurrentMenu.Open = not Value
                    end
                    Menu:UpdateInstructionalButtons(Value);
                    Menu:UpdateCursorStyle();
                    FlashSideUI.CurrentMenu = Menu
                    menuOpen = true
                else
                    menuOpen = false
                    FlashSideUI.CurrentMenu = nil
                end
                Menu.Open = Value
                FlashSideUI.Options = 0
                FlashSideUI.ItemOffset = 0
                FlashSideUI.LastControl = false
            else
                return Menu.Open
            end
        end
    end
end 

function FlashSideUI.CloseAll()
    menuOpen = false

    if FlashSideUI.CurrentMenu ~= nil then
        local parent = FlashSideUI.CurrentMenu.Parent
        while parent ~= nil do
            parent.Index = 1
            parent.Pagination.Minimum = 1
            parent.Pagination.Maximum = 10
            parent = parent.Parent
        end
        FlashSideUI.CurrentMenu.Index = 1
        FlashSideUI.CurrentMenu.Pagination.Minimum = 1
        FlashSideUI.CurrentMenu.Pagination.Maximum = 10
        FlashSideUI.CurrentMenu.Open = false
        FlashSideUI.CurrentMenu = nil
    end
    FlashSideUI.Options = 0
    FlashSideUI.ItemOffset = 0
    ResetScriptGfxAlign()
end

