---@public
function FlashSideUI.Subtitle()
    if FlashSideUI.CurrentMenu ~= nil then
        if FlashSideUI.CurrentMenu() then
            FlashSideUI.ItemsSafeZone(FlashSideUI.CurrentMenu)
            if FlashSideUI.CurrentMenu.Subtitle ~= "" then
                RenderRectangle(FlashSideUI.CurrentMenu.X, FlashSideUI.CurrentMenu.Y + FlashSideUI.ItemOffset, FlashSideUI.Settings.Items.Subtitle.Background.Width + FlashSideUI.CurrentMenu.WidthOffset, FlashSideUI.Settings.Items.Subtitle.Background.Height + FlashSideUI.CurrentMenu.SubtitleHeight, 0, 0, 0, 255)
                RenderText(FlashSideUI.CurrentMenu.Subtitle, FlashSideUI.CurrentMenu.X + FlashSideUI.Settings.Items.Subtitle.Text.X, FlashSideUI.CurrentMenu.Y + FlashSideUI.Settings.Items.Subtitle.Text.Y + FlashSideUI.ItemOffset, 0, FlashSideUI.Settings.Items.Subtitle.Text.Scale, 245, 245, 245, 255, nil, false, false, FlashSideUI.Settings.Items.Subtitle.Background.Width + FlashSideUI.CurrentMenu.WidthOffset)
                if FlashSideUI.CurrentMenu.Index > FlashSideUI.CurrentMenu.Options or FlashSideUI.CurrentMenu.Index < 0 then
                    FlashSideUI.CurrentMenu.Index = 1
                end
                FlashSideUI.RefreshPagination()
                if FlashSideUI.CurrentMenu.PageCounter == nil then
                    RenderText(FlashSideUI.CurrentMenu.PageCounterColour .. FlashSideUI.CurrentMenu.Index .. " / " .. FlashSideUI.CurrentMenu.Options, FlashSideUI.CurrentMenu.X + FlashSideUI.Settings.Items.Subtitle.PreText.X + FlashSideUI.CurrentMenu.WidthOffset, FlashSideUI.CurrentMenu.Y + FlashSideUI.Settings.Items.Subtitle.PreText.Y + FlashSideUI.ItemOffset, 0, FlashSideUI.Settings.Items.Subtitle.PreText.Scale, 245, 245, 245, 255, 2)
                else
