---Render
---@param instructionalButton boolean
---@return nil
---@public
function FlashSideUI.Render(instructionalButton)
    if FlashSideUI.CurrentMenu ~= nil then
        if FlashSideUI.CurrentMenu() then
            if FlashSideUI.CurrentMenu.Safezone then
                ResetScriptGfxAlign()
            end
            if (instructionalButton) then
                DrawScaleformMovieFullscreen(FlashSideUI.CurrentMenu.InstructionalScaleform, 255, 255, 255, 255, 0)
            end
            FlashSideUI.CurrentMenu.Options = FlashSideUI.Options
            FlashSideUI.CurrentMenu.SafeZoneSize = nil
            FlashSideUI.Controls()
            FlashSideUI.Options = 0
            FlashSideUI.StatisticPanelCount = 0
            FlashSideUI.ItemOffset = 0
            if FlashSideUI.CurrentMenu.Controls.Back.Enabled and FlashSideUI.CurrentMenu.Closable then
                if FlashSideUI.CurrentMenu.Controls.Back.Pressed then
                    FlashSideUI.CurrentMenu.Controls.Back.Pressed = false
                    local Audio = FlashSideUI.Settings.Audio
                    FlashSideUI.PlaySound(Audio[Audio.Use].Back.audioName, Audio[Audio.Use].Back.audioRef)
                    if FlashSideUI.CurrentMenu.Closed ~= nil then
                        collectgarbage()
                        FlashSideUI.CurrentMenu.Closed()
                    end
                    if FlashSideUI.CurrentMenu.Parent ~= nil then
                        if FlashSideUI.CurrentMenu.Parent() then
                            FlashSideUI.NextMenu = FlashSideUI.CurrentMenu.Parent
                            FlashSideUI.CurrentMenu:UpdateCursorStyle()
                        else
                            --print('xxx') Debug print
                            FlashSideUI.NextMenu = nil
                            FlashSideUI.Visible(FlashSideUI.CurrentMenu, false)
                        end
                    else
                        --print('zz') Debug print
                        FlashSideUI.NextMenu = nil
                        FlashSideUI.Visible(FlashSideUI.CurrentMenu, false)
                    end
                end
            end
            if FlashSideUI.NextMenu ~= nil then
                if FlashSideUI.NextMenu() then
                    FlashSideUI.Visible(FlashSideUI.CurrentMenu, false)
                    FlashSideUI.Visible(FlashSideUI.NextMenu, true)
                    FlashSideUI.CurrentMenu.Controls.Select.Active = false
                    FlashSideUI.NextMenu = nil
                    FlashSideUI.LastControl = false
                end
            end
        end
    end
end

---ItemsDescription
---@param CurrentMenu table
---@param Description string
---@param Selected boolean
---@return nil
---@public
function FlashSideUI.ItemsDescription(CurrentMenu, Description, Selected)
    ---@type table
    if Description ~= "" or Description ~= nil then
        local SettingsDescription = FlashSideUI.Settings.Items.Description;
        if Selected and CurrentMenu.Description ~= Description then
            CurrentMenu.Description = Description or nil
            ---@type number
            local DescriptionLineCount = GetLineCount(CurrentMenu.Description, CurrentMenu.X + SettingsDescription.Text.X, CurrentMenu.Y + SettingsDescription.Text.Y + CurrentMenu.SubtitleHeight + FlashSideUI.ItemOffset, 0, SettingsDescription.Text.Scale, 255, 255, 255, 255, nil, false, false, SettingsDescription.Background.Width + (CurrentMenu.WidthOffset - 5.0))
            if DescriptionLineCount > 1 then
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height * DescriptionLineCount
            else
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height + 7
            end
        end
    end
end

---MouseBounds
---@param CurrentMenu table
---@param Selected boolean
---@param Option number
---@param SettingsButton table
---@return boolean
---@public
function FlashSideUI.ItemsMouseBounds(CurrentMenu, Selected, Option, SettingsButton)
    ---@type boolean
    local Hovered = false
    Hovered = FlashSideUI.IsMouseInBounds(CurrentMenu.X + CurrentMenu.SafeZoneSize.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SafeZoneSize.Y + CurrentMenu.SubtitleHeight + FlashSideUI.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height)
    if Hovered and not Selected then
        RenderRectangle(CurrentMenu.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SubtitleHeight + FlashSideUI.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height, 255, 255, 255, 20)
        if CurrentMenu.Controls.Click.Active then
            CurrentMenu.Index = Option
            local Audio = FlashSideUI.Settings.Audio
            FlashSideUI.PlaySound(Audio[Audio.Use].Error.audioName, Audio[Audio.Use].Error.audioRef)
        end
    end
    return Hovered;
end

---ItemsSafeZone
---@param CurrentMenu table
---@return nil
---@public
function FlashSideUI.ItemsSafeZone(CurrentMenu)
    if not CurrentMenu.SafeZoneSize then
        CurrentMenu.SafeZoneSize = { X = 0, Y = 0 }
        if CurrentMenu.Safezone then
            CurrentMenu.SafeZoneSize = FlashSideUI.GetSafeZoneBounds()
            SetScriptGfxAlign(76, 84)
            SetScriptGfxAlignParams(0, 0, 0, 0)
        end
    end
end

function FlashSideUI.CurrentIsEqualTo(Current, To, Style, DefaultStyle)
    if (Current == To) then
        return Style;
    else
        return DefaultStyle or {};
    end
end

function FlashSideUI.RefreshPagination()
    if (FlashSideUI.CurrentMenu ~= nil) then
        if (FlashSideUI.CurrentMenu.Index > 10) then
            local offset = FlashSideUI.CurrentMenu.Index - 10
            FlashSideUI.CurrentMenu.Pagination.Minimum = 1 + offset
            FlashSideUI.CurrentMenu.Pagination.Maximum = 10 + offset
        else
            FlashSideUI.CurrentMenu.Pagination.Minimum = 1
            FlashSideUI.CurrentMenu.Pagination.Maximum = 10
        end
    end
end

function FlashSideUI.IsVisible(menu, header, glare, instructional, items, panels)
    if (FlashSideUI.Visible(menu)) then
        if (header == true) then
            FlashSideUI.Banner(true, glare or false)
        end
        FlashSideUI.Subtitle()
        if (items ~= nil) then
            items()
        end
        FlashSideUI.Background();
        FlashSideUI.Navigation();
        FlashSideUI.Description();
        if (panels ~= nil) then
            panels()
        end
        FlashSideUI.Render(instructional or false)
    end
end


---CreateWhile
---@param wait number
---@param menu table
---@param key number
---@param closure function
---@return void
---@public
function FlashSideUI.CreateWhile(wait, menu, key, closure)
    Citizen.CreateThread(function()
        while (true) do
            Citizen.Wait(wait or 0.1)

            if (key ~= nil) then
                if IsControlJustPressed(1, key) then
                    FlashSideUI.Visible(menu, not FlashSideUI.Visible(menu))
                end
            end

            closure()
        end
    end)
end

